import express from "express";
import createError from "http-errors";
import cookieParser from "cookie-parser";
import logger from "morgan";
import dotenv from "dotenv";

dotenv.config();

import indexRouter from "./routes/index";
import usersRouter from "./routes/users";

const app = express();

app.use(logger("dev"));
app.use(express.json());
app.use(express.urlencoded({ extended: false }));
app.use(cookieParser());

app.use("/api/books", indexRouter);
app.use("/users", usersRouter);

app.get("*", (req, res) => {
  res.json({ page: "Try a valid api" });
});

// catch 404 and forward to error handler
app.use(function (req, res, next) {
  next(createError(404));
});

// error handler
app.use(function (err, req, res, _next) {
  // set locals, only providing error in development
  res.locals.message = err.message;
  res.locals.error = req.app.get("env") === "development" ? err : {};

  // render the error page
  res.status(err.status || 500);
  res.json({ error: err.message });
});

export default app;

import { Router } from "express";
import {
  getAllBooks,
  addBook,
  updateBook,
  getABook,
  deleteBook,
} from "../controllers/bookcontroller";
import { bookValidation } from "../validation/bookvalidation";

// import BookQuery from "../controllers/BookControllerWithClass";

const router = Router();

router.get("/", async (_req, res, _next) => {
  const books = await getAllBooks();
  res.json({ books });
});

router.get("/:singleID", async (req, res, _next) => {
  const singleID = req.params.singleID;
  if (isNaN(singleID))
    return res.status(404).json({ message: "Not a valid id" });
  const book = await getABook(singleID);
  if (book === null) return res.status(404).json({ message: "Book not found" });
  res.json({ book });
});

router.post("/", async (req, res, _next) => {
  const { error, body } = bookValidation(req.body);
  if (error.length > 0) return res.status(400).json({ error });
  const addedBook = await addBook(body);
  res.json({ book: addedBook, message: "Added Successfully!!!" });
});

router.patch("/:upID", async (req, res, _next) => {
  const upID = req.params.upID;
  if (isNaN(upID)) return res.status(404).json({ message: "Not a valid id" });
  const updatedBook = await updateBook(req.body, upID);
  if (updatedBook === undefined)
    return res.status(404).json({ message: "Book not found" });
  res.json({ book: updatedBook });
});

router.delete("/:deleteID", async (req, res) => {
  const deleteId = req.params.deleteID;
  if (isNaN(deleteId))
    return res.status(404).json({ message: "Not a valid id" });
  const deletedBook = await deleteBook(deleteId);
  if (deletedBook === undefined)
    return res.status(404).json({ message: "Book not found" });
  return res.json({
    message: "Deleted successfully!!!",
  });
});

export default router;

import db from "../database/models/index";
import { Op } from "sequelize";

export const getAllBooks = async () => {
  try {
    return await db.Book.findAll();
  } catch (error) {
    return error.message;
  }
};

export const getABook = async (id) => {
  try {
    return await db.Book.findOne({
      where: { id: Number(id) },
    });
    // const oneBook = await db.Book.findAll({
    //   where: { [Op.or]: [{ id: Number(id) }, { price: "#2000" }] },
    // });
  } catch (error) {
    return error.message;
  }
};

export const addBook = async (book) => {
  try {
    return await db.Book.create(book);
  } catch (error) {
    return error.message;
  }
};

export const updateBook = async (book, id) => {
  const found = await db.Book.findOne({ where: { id: Number(id) } });
  try {
    if (found) {
      const updatedBook = await db.Book.update(book, {
        where: { id: Number(id) },
      });
      if (updatedBook) return book;
    }
  } catch (error) {
    return error.message;
  }
};

export const deleteBook = async (id) => {
  const found = await db.Book.findOne({ where: { id: Number(id) } });
  try {
    if (found) {
      return await db.Book.destroy({
        where: { id: Number(id) },
      });
    }
  } catch (error) {
    return error.message;
  }
};

const fs = require("fs");
const path = require("path");
const Sequelize = require("sequelize");
const envConfigs = require("../config/config");

const basename = path.basename(__filename);
const env = process.env.NODE_ENV || "development";
const config = envConfigs[env];
const db = {};

let sequelize;
if (config.url) {
  sequelize = new Sequelize(config.url, config);
} else {
  sequelize = new Sequelize(
    config.database,
    config.username,
    config.password,
    config
  );
}

fs.readdirSync(__dirname)
  .filter((file) => {
    return (
      file.indexOf(".") !== 0 && file !== basename && file.slice(-3) === ".js"
    );
  })
  .forEach((file) => {
    const model = sequelize["import"](path.join(__dirname, file));
    db[model.name] = model;
  });

Object.keys(db).forEach((modelName) => {
  if (db[modelName].associate) {
    db[modelName].associate(db);
  }
});

db.sequelize = sequelize;
db.Sequelize = Sequelize;

db.sequelize.authenticate().then(() => console.log("Connected to database"));

export default db;

